import React, { Component } from 'react'
import Slider from "react-slick";


class Carousel extends Component {
    constructor(props) {
        super(props);
        this.state = { 
        datas:[]
        }
    }
    componentDidMount(){

        let data=this.props.data;
        var ele =document.getElementsByClassName("slick-arrow");
        var prev=document.getElementsByClassName("slick-prev");
        prev[0].classList.add("prvbtn");
        
        var prev=document.getElementsByClassName("slick-next");
        prev[0].classList.add("nextbtn");
      
        let listul=document.getElementsByClassName("slick-dots");
        for (var j = 0; j < listul.length; j++ ) {
          listul[j].style.display = "none";
    
        }

        let datas=[];
       for(let i=0;i<data.length;i++)
       {
           if(data[i].mealType.title===this.props.type)
          {
              datas.push({
                image:data[i].image,
                title:data[i].title,
                description:data[i].description,
                prepa:this.get_preparation(data[i].preparation,data[i].fee),
                rate:data[i].rate,
                delivery:data[i].delivery
              })
          }
       }

       let newstate=[this.state];
       newstate[0].datas=datas;
       this.setState({ newstate });
      }

      get_preparation=(preparation,fee)=>{
          return "10-20 min";
      }
    render() { 
        var settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 3,
            slidesToScroll: 1
          };
        let cards=[];
        cards=this.state.datas.map((d,index)=>(
            <div key={index} >
            <img className="c_img" src={d.image} />
            <div className="mt-2">
            {d.title}
            </div>
            <div className="text-muted">
            {d.description}
            </div>
            <div className="details">
                <div className="prepa">{this.get_preparation()}</div>
                <div className="rate">{this.rate} 
                <img src="https://d3i4yxtzktqr9n.cloudfront.net/web-eats-v2/972e1cb487b3a5c72c30a6635596f477.svg"></img>
                </div>
                <div className="delivery">{d.delivery}</div>
            </div>
            </div>
        ));
        return ( 
        <React.Fragment>
             <Slider {...settings}>
                 {cards}
      </Slider>
        </React.Fragment> 
        );
    }
}
 
export default Carousel;
